jQuery(document).ready(function (e) {
    "use strict";
    e(function () {
        e("#contact").validate({
            rules: { name: { required: !0, minlength: 2 }, surname: { required: !0, minlength: 2 }, email: { required: !0, email: !0 }, subject: { required: !0 }, message: { required: !0 } },
            messages: {
                name: { required: "Lütfen adınızı yazın", minlength: "Lütfen gerçek adınızı yazın" },
                surname: { required: "Lütfen soyadınızı yazın", minlength: "Lütfen gerçek soyadınızı yazın" },
                email: { required: "Lütfen e-posta adresinizi doğru girin" },
                subject: { required: "Lütfen konu girin", minlength: "Konu çok kısa" },
                message: { required: "Lütfen mesajınızı yazın", minlength: "Mesaj çok kısa" },
            },
            submitHandler: function (r) {
                e(r).ajaxSubmit({
                    type: "POST",
                    data: e(r).serialize(),
                    url: "php/process.php",
                    success: function () {
                        e("#contact :input").attr("disabled", "disabled"),
                            e("#contact").fadeTo("slow", 0.15, function () {
                                e(this).find(":input").attr("disabled", "disabled"), e(this).find("label").css("cursor", "default"), e("#success").fadeIn();
                            });
                    },
                    error: function () {
                        e("#contact").fadeTo("slow", 0.15, function () {
                            e("#error").fadeIn();
                        });
                    },
                });
            },
        });
    });
});
